sections.css:
@font-face {
    font-family: 'icomoon';
    src:    url('../fonts/icomoon.eot?rl2uld');
    src:    url('../fonts/icomoon.eot?rl2uld#iefix') format('embedded-opentype'),
        url('../fonts/icomoon.ttf?rl2uld') format('truetype'),
        url('../fonts/icomoon.woff?rl2uld') format('woff'),
        url('../fonts/icomoon.svg?rl2uld#icomoon') format('svg');
    font-weight: normal;
    font-style: normal;
}

[class^="icon-"], [class*=" icon-"] {
    /* use !important to prevent issues with browser extensions that change fonts */
    font-family: 'icomoon' !important;
    speak: none;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;

    /* Better Font Rendering =========== */
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.icon-home:before {
    content: "\e900";
}
.icon-home2:before {
    content: "\e901";
}
.icon-home3:before {
    content: "\e902";
}
.icon-office:before {
    content: "\e903";
}
.icon-newspaper:before {
    content: "\e904";
}
.icon-pencil:before {
    content: "\e905";
}
.icon-pencil2:before {
    content: "\e906";
}
.icon-quill:before {
    content: "\e907";
}
.icon-pen:before {
    content: "\e908";
}
.icon-blog:before {
    content: "\e909";
}
.icon-eyedropper:before {
    content: "\e90a";
}
.icon-droplet:before {
    content: "\e90b";
}

h1,
h2,
h3,
h4,
h5 {
  font-family: $helvetica;
  font-size: 14px;
  line-height: 1.45;
  color: #222;
  font-weight: 400;
  font-weight: normal;
  font-style: normal;
  line-height: 1.2;
  margin: 1.414em 0 .5em;
  color: #444;
}

h1 {
  font-size: 3.157em;
  margin-top: .5em;
}

h2 {
  font-size: 2.369em;
}

h3 {
  font-size: 1.777em;
}

h4 {
  font-size: 1.333em;
}

h4,
h5 {
  font-weight: 500;
}

@import "components/btn.component";
@import "components/forms.component";

// trumps here
// u sure u need this?

.Btn {
  display: inline-block;
  border-radius: 4px;
  font-size: 14px;
  font-family: $arial;
  text-align: center;
  text-decoration: none;
  border: 0;
  cursor: pointer;
  padding: 6px 12px;
}

.Btn--primary {
  background: $green-sushi;
  color: white;
}

.Btn--primary.Btn--inverse {
  border: 1px solid $green-sushi;
  color: $green-sushi;
  background: white;
}

.Btn--secondary {
  background: $red-pink;
  color: white;
}
.Btn--secondary.Btn--inverse {
  border: 1px solid $red-pink;
  color: $red-pink;
  background: white;
}

.Btn--block {
  display: block;
  width: 100%;
}

.Form {
  &-group {
    margin-bottom: 15px
  }

  &-label {
    display: inline-block;
    max-width: 100%;
    margin-bottom: 5px;
    font-weight: 700;
    font-family: $helvetica;
  }

  &-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 3px 5px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #fff;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
  }
}

